import smtplib
from email.mime.text import MIMEText
from email.mime.multipart import MIMEMultipart
from .config import settings



def static_html_for_waitlist():
    return static_html_text


def send_email(to_email, subject, message=None, html=False):
    # Create the MIME object
    msg = MIMEMultipart()
    msg['From'] = settings.zoho_smtp_sender_email
    msg['To'] = to_email
    msg['Subject'] = subject

    # Attach the message to the email
    msg.attach(MIMEText(message, 'html' if html else 'plain'))

    # Connect to the Zoho SMTP server and send the email
    with smtplib.SMTP(settings.zoho_smtp_outgoing_server_name, settings.zoho_smtp_port_with_tls) as server:
        server.starttls()
        server.login(settings.zoho_smtp_username, settings.zoho_smtp_password)
        server.sendmail(settings.zoho_smtp_sender_email,
                        to_email, msg.as_string())


